class Solution {
public:
    int ans = INT_MAX;
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        dfs(price, special, needs, 0);
        return ans;
    }
private:
    void dfs(vector<int>& price, vector<vector<int>>& special, vector<int>& needs, int currPrice) {
        if (accumulate(needs.begin(), needs.end(), 0) == 0) {
            ans = min(ans, currPrice);
            return;
        }
        int len = special.size();
        for (int i = 0; i < len; i++) {
            if (canSpecialBeUsed(needs, special[i])) {
                currPrice += special[i].back();
                needs = updateNeeds(needs, special[i], "-");
                dfs(price, special, needs, currPrice);
                currPrice -= special[i].back();
                needs = updateNeeds(needs, special[i], "+");
            }
        }
        if (accumulate(needs.begin(), needs.end(), 0) != 0) {
            currPrice += addLeftOverItems(needs, price);
            ans = min(ans, currPrice);
        }
    }

    bool canSpecialBeUsed(vector<int>& needs, vector<int>& special) {
        int len = needs.size();
        for (int i = 0; i < len; i++) {
            if (special[i] > needs[i]) {
                return false;
            }
        }
        return true;
    }

    vector<int>& updateNeeds(vector<int>& needs, vector<int>& special, string op) {
        int len = needs.size();
        for (int i = 0; i < len; i++) {
            if (op == "-") {
                needs[i] -= special[i];
            } else {
                needs[i] += special[i];
            }
        }
        return needs;
    }

    int addLeftOverItems(vector<int>& needs, vector<int>& price) {
        int len = needs.size();
        int sum = 0;
        for (int i = 0; i < len; i++) {
            sum += needs[i] * price[i];
        }
        return sum;
    }

};
