class Solution {
public:
    map<vector<int>,int> mp; 
    int func(vector<int>& price, vector<vector<int>>& special, vector<int> needs, int &n, int &m){
        int cnt=0;
        for(int i=0;i<n;i++){
            if(needs[i]==0){
                cnt++;
            }
        }

        if(cnt==n)
            return 0;

        if(mp.find(needs)!=mp.end()){
            return mp[needs];
        }


        int mn=INT_MAX;
        int len=special[0].size();
        vector<int> v(n,0);
        for(int i=0;i<m;i++){
            bool flag=true;
            for(int j=0;j<len-1;j++){
                if(needs[j]-special[i][j]<0){
                    flag=false;
                }
                v[j]=needs[j]-special[i][j];
            }

            if(flag){
                mn=min(mn,special[i][len-1]+func(price,special,v,n,m));
            }

        }

        int val=0;
        for(int i=0;i<n;i++){
            if(needs[i]){
                val=val+price[i];
                v[i]=needs[i]-1;
            }else{
                v[i]=0;
            }
        }

        mn=min(mn,val+func(price,special,v,n,m));
        return mp[needs]=mn;

    }
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        int n=price.size();
        int m=special.size();
        return func(price,special,needs,n,m);
    }
};
