class Solution {
public:

    int shoppingOffersHelper(vector<int>&price, vector<vector<int>>&spl, vector<int>&needs, map<vector<int>,int>&m){
        bool flag = false;
        for(int i=0; i<needs.size(); i++){
            if(needs[i] < 0)
                return INT_MAX;
            if(needs[i] > 0)
                flag = true;
        }

        if(!flag)
            return 0;

        if(m.find(needs) != m.end())
            return m[needs];

        long minCost = INT_MAX;
        int cost = 0;
        for(int i=0; i<price.size(); i++){
            cost += price[i] * needs[i];
        }

        minCost = min(minCost, (long)cost);

        for(int i=0; i<spl.size(); i++){
            for(int j=0; j<price.size(); j++)
                needs[j] -= spl[i][j];
                minCost = min(minCost, (long)spl[i][price.size()] + shoppingOffersHelper(price, spl, needs, m));
            for(int j=0; j<price.size(); j++)
                needs[j] += spl[i][j];

        }

        m[needs] = (int)minCost;
        return (int)minCost;


    }


    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {

        map<vector<int>, int>m;
        return shoppingOffersHelper(price, special, needs, m);

    }
};
