public class Solution
	{
		public int ShoppingOffers(IList<int> price, IList<IList<int>> special, IList<int> needs)
		{
			int cost = GetCost(price, needs);

			if (cost == 0)
				return 0;

			for(int i = 0; i < special.Count; i++)
			{
				IList<int> temp = needs.ToList();
				(bool, int) res = CheckIfApplicable(special[i], temp);

				if (res.Item1)
					cost = Math.Min(cost, ShoppingOffers(price, special, temp) + res.Item2);
			}

			return cost;
		}

		public int GetCost(IList<int> price, IList<int> needs)
		{
			int cost = 0;

			for(int i = 0; i < price.Count; i++)
				cost += (price[i] * needs[i]);

			return cost;
		}

		public (bool, int) CheckIfApplicable(IList<int> special, IList<int> needs)
		{
			for(int i = 0; i < needs.Count; i++)
			{
				if (needs[i] < special[i])
					return (false, 0);

				needs[i] -= special[i];
			}

			return (true, special[special.Count - 1]);
		}
	}

