public int shoppingOffers(List<Integer> price, List<List<Integer>> special, List<Integer> needs)
	{
		int localMin = getDirectBuyCost(price, needs);

		for (List<Integer> offer : special)
		{
			List<Integer> needsLeft = checkNeeds(needs, offer);
			if (!needsLeft.isEmpty()) {
				localMin = Math.min(localMin, offer.get(offer.size() - 1) + shoppingOffers(price, special, needsLeft));
			}
		}

		return localMin;
	}

	private int getDirectBuyCost(List<Integer> price, List<Integer> needs)
	{
		int cost = 0;
		for (int i = 0; i < price.size(); ++i)
		{
			cost += (price.get(i) * needs.get(i));
		}

		return cost;
	}

	private List<Integer> checkNeeds(List<Integer> needs, List<Integer> offer)
	{
		int len = needs.size();
		List<Integer> needsLeft = new ArrayList<>();
		for (int i = 0; i < len; ++i) {
			int left = needs.get(i) - offer.get(i);
			if (left < 0) return new ArrayList<>();
			needsLeft.add(left);
		}

		return needsLeft;
	}

