class Solution {
public:
    int ans = 0;
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {

        for(int i = 0; i < needs.size(); i++)
            ans += price[i] * needs[i];

        solve(price, special, needs, 0);
        return ans;
    }

    void solve(vector<int>& price, vector<vector<int>>& special, vector<int> needs, int cost)
    {
        if(check(needs))
        {
            ans = min(ans, cost);
            return;
        }

        if(cost >= ans)
            return;

        bool flag = false;
        for(int i = 0; i < special.size(); i++)
        {
            vector <int> temp = needs;
            for(int j = 0; j < needs.size(); j++)
            {
                needs[j] = needs[j] - special[i][j];

                if(needs[j] < 0)
                {
                    flag = true;
                    needs = temp;
                    break;
                }
            }

            if(flag)
            {
                flag = false;
                continue;
            }

            solve(price, special, needs, cost + special[i].back());
            needs = temp;

        }

        int res = 0;
        for(int i = 0; i < needs.size(); i++)
        {
            int x = needs[i];
            needs[i] = 0;

            res += x * price[i];
        }

        solve(price, special, needs, cost + res);

    }//[1,2,1]
     //[2,3,4]
    bool check(vector <int>& needs)
    {
        int count0 = count(needs.begin(), needs.end(), 0);

        return count0 == needs.size();
    }
};
