var shoppingOffers = function(price, special, needs) {
    const check = (need) => {
        let pay = 0;
        for (let i = 0; i < price.length; i++) {
            pay += price[i] * need[i];
        }

        for(let spe of special) {   
            let canUse = true;
            for (let i = 0; i < spe.length; i++) {
                if (need[i] < spe[i]) {
                    canUse = false;
                    break;
                }
            }
            if (canUse) {
                let newNeed = [];
                for(let i = 0; i < spe.length - 1; i++) {
                    newNeed.push(need[i] - spe[i]);
                }
                let totalPrice = spe[spe.length - 1] + check(newNeed);
                if (totalPrice < pay) {
                    pay = totalPrice;
                }
            }
        }

        return pay;
    }

    return check(needs);
};

