class Solution {
public:
    bool check(const vector<int>&A,const vector<int>&B)
   {
       for(int i=0;i<A.size();++i)
       {
           if(B[i]>A[i]) return 0;
       }
       return 1;
   }
    vector<int> update(const vector<int>&A,const vector<int>&B)
    {
        vector<int> v(A.size(),0);
        for(int i=0;i<A.size();++i)
            v[i]=A[i]-B[i];
        return v;
    }
int shoppingOffers(vector<int>& A, vector<vector<int>>& B, vector<int>& n){
        int sze = A.size(), ans=0;

        for(int i=0;i<A.size();++i)    // buying all items without offers 
            ans+=A[i]*n[i];

        queue<pair<vector<int>,int >> q;
        q.push({n,0});

        while(!q.empty())
        {
            pair<vector<int>,int> t = q.front(); 
            q.pop();
            bool updated=1;
            for(int i=0;i<B.size();++i)
            {
                if(check(t.first,B[i]))
                {
                    vector<int> k = update(t.first,B[i]);
                    q.push({k,t.second + B[i][sze]});
                    updated = 0; 
                }
            }
            if(updated)
            {
                // we couldnt apply any offer so we have to purchase sole items 
                // or we have no items left to purchase , in that case we need the cost only
                for(int i=0;i<t.first.size();++i)
                    t.second +=t.first[i]*A[i];
                ans=min(ans,t.second);
            }
        }
        return ans;
    }
};

