class Solution {
    int n;
    List<List<Integer>> special;
    List<Integer> price;
    List<Integer> needs;
    HashMap<List<Integer>, Integer> memo = new HashMap();
    public int shoppingOffers(List<Integer> price, List<List<Integer>> special, List<Integer> needs) {
        n = price.size();
        this.price = price;
        this.needs = needs;
        this.special = special;
        return dfs();
    }

    public int dfs() {
        if(memo.containsKey(needs)) return memo.get(needs);

        // Let's get the max cost at this point without any offer.
        int cost = finalCost();

        for(List<Integer> sp: special) {
            int size = sp.size();
            boolean takeOffer = true;
            int offerPrice = sp.get(size - 1);
            for(int i = 0; i < size - 1; i++) {
                // can't take this offer, quantity limit execeeded
                if(needs.get(i) < sp.get(i)) {
                    takeOffer = false;
                    break;
                }
            }

            if(takeOffer) {
                // backtracking
                setNeed(sp, true);
                int val = offerPrice + dfs();
                cost = Math.min(cost, val);
                setNeed(sp, false);
            } 
        }
        memo.put(needs, cost);        
        return cost;
    }

    public int finalCost() {
        int cost = 0;
        for(int i = 0; i < n; i++) {
            int val = price.get(i) * needs.get(i);
            cost += val;
        }
        return cost;
    }

    public void setNeed(List<Integer> offer, boolean addOffer) {
        for(int i = 0; i < offer.size() - 1; i++) {
            int val = 0;
            if(addOffer)
                val = needs.get(i) - offer.get(i);
            else
                val = needs.get(i) + offer.get(i);
            needs.set(i, val);
        }
    }
}
