class Solution {
public:
	int dfs(vector<int>& price, vector<vector<int>>& special, vector<int>& needs, vector<int>& dp, int j) {
		// n -> number of items
		int n = price.size();

		// curNum -> current items purchased
		vector<int> curNum(n);

		int tmp = j;
		for (int k = 0; k < n; ++k) {
			curNum[k] = tmp % (needs[k] + 1);
			tmp /= (needs[k] + 1);
			if (!tmp) break;
			}

		// consider offer one by one
		for (auto offer : special) {
			bool can = true;
			int diff = 0;	

			// check whether offer can be taken or not
			int mul = 1;
			for (int i = 0; i < n; ++i) {
				diff += offer[i] * mul;
				mul *= needs[i] + 1;
				if (offer[i] > curNum[i]) {can = false; break;}
			}

			// if offer is a valid option
			if (can) {
				dfs(price, special, needs, dp, j - diff);
				dp[j] = min(dp[j-diff] + offer[n], dp[j]);
			}
		}

		return 0;
	}

    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
		// n -> number of items
		int n = price.size();

		// construct the serialized dp	
		int mul = 1;
		for (int need : needs) mul *= (need + 1);

		vector<int> dp(mul);

		// dp base -> purchase without offers
		for (int i = 0; i < mul; ++i) {
			int tmp = i, cost = 0;
			for (int k = 0; k < n; ++k) {
				cost += tmp % (needs[k] + 1) * price[k];
				tmp /= (needs[k] + 1);
			}

			dp[i] = cost;
		}

		// update dp in a dfs way
		dfs(price, special, needs, dp, mul-1);

		return dp[mul-1]; 
	}
};
