int shoppingOffers_(string target, vector<int>& price, vector<vector<int>>& special, unordered_map<string, int>& m) {

	if (m.count(target)) return m[target];

	int res = 0;

	for (int i = 0; i < target.length(); ++i) res += price[i] * (target[i] - '0');

	for (vector<int>& vec : special) {
		string tmp = target;
		int i = 0;
		for (; i < target.length(); ++i) {
			if (target[i] - '0' >=  vec[i]) tmp[i] -= vec[i];
			else break;
		}
		if (i == target.length()) res = min(res, vec.back() + shoppingOffers_(tmp, price, special, m));
	}

	m[target] = res;
	return res;

}

int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {

	unordered_map<string, int> m;
	string target = "";
	for (int& need : needs) target.push_back('0' + need);
	return shoppingOffers_(target, price, special, m);
}
