```var shoppingOffers = function(price, special, needs) {
    var directBuy=function(price, needs){
        let res=0;
        for(let i=0; i<price.length; i++){
            res+= price[i]*needs[i];
        }
        return res;
    }
    var isValid=function(offer, needs){
        for(let i=0; i<offer.length; i++){
            if(offer[i]> needs[i]) return false;
        }
         return true;
    }
    var help= (price, special, needs) => {
        let curMin=directBuy(price, needs);
        for (let i=0; i< special.length; i++){
            let curOf= special[i];
            if(isValid(curOf, needs)){
                let tem=[];
                for(let j=0; j<needs.length; j++){
                    tem.push(needs[j] - curOf[j]);
                }
                if(tem.length>0){
                    curMin = Math.min(curMin, curOf[curOf.length-1]+ help(price, special, tem))
                }
            }
        }
        return curMin;
    }
    return help(price, special, needs)
};

