from collections import deque
from collections import defaultdict
class Solution(object):
    def shoppingOffers(self, price, special, needs):
        """
        :type price: List[int]
        :type special: List[List[int]]
        :type needs: List[int]
        :rtype: int
        """
        def neighbor(state,cost):
            for s in special:
                items,p = s[:-1],s[-1]
                new_cost,new_state = cost,list(state)
                for i,num in enumerate(items):
                    if new_state[i]+num<=needs[i]:
                        new_state[i] = new_state[i]+num
                    else:
                        break
                else:
                    new_cost = cost+p
                    yield (new_cost,tuple(new_state))
        def add(state,cost):
            for i,num in enumerate(state):
                cost+= price[i] *(needs[i] - num)
            return cost

        ans = float("inf")

        initial_state = tuple([0 for _ in needs])
        dists = defaultdict(lambda:float('inf'))

        q = deque()
        q.append((0,initial_state))


        while q:
            cost,state = q.popleft()
            if cost>dists[state] or cost>=ans:
                continue
            real_cost = add(state,cost)
            ans = min(real_cost , ans)
            for c_cost,c_state in neighbor(state,cost):
                if c_cost>=dists[c_state]:
                    continue
                dists[c_state] = c_cost
                q.append((c_cost,c_state))
        return ans

