public int shoppingOffers(List<Integer> price, List<List<Integer>> special, List<Integer> needs) {
        HashMap<Integer, Integer> map = new HashMap<>();
        int[] arr = new int[needs.size()];
        int key = hash(arr);
        map.put(key, 0);
        for (int i = 0; i < needs.size(); i++) arr[i] = needs.get(i);
        return helper(map, price, special, arr);
    }

    private int helper(HashMap<Integer, Integer> map,List<Integer> price, 
                       List<List<Integer>> special, int[] needs) {
        int key = hash(needs);
        if (map.containsKey(key)) return map.get(key);
        int sum = 0;
        for (int i = 0; i < needs.length; i++) sum += price.get(i) * needs[i];
        for (List<Integer> l : special) {
            boolean flag = true;
            for (int i = 0; i <  needs.length; i++) {
                if (needs[i] - l.get(i) < 0) {
                    flag = false;
                    break;
                }
            }
            if (flag) {
                for (int i = 0; i <  needs.length; i++) needs[i] -= l.get(i);
                sum = Math.min(sum, l.get(l.size() - 1) + helper(map, price, special, needs));
                for (int i = 0; i <  needs.length; i++) needs[i] += l.get(i);
