    def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:
        if not price or not needs:
            return 0

        n = len(needs)
        dp = {}

        def canShop(offer, needs):
            for i in range(n):
                if needs[i] < offer[i]:
                    return False
            return True

        def findLowestPriceMemo(needs):
            key = str(needs)

            if key in dp:
                return dp[key]

            notpick = sum(price[i] * needs[i] for i in range(n))
            pick = float('inf')

            for offer in special:
                offerPrice = offer[-1]

                if canShop(offer, needs):
                    new_needs = [needs[i] - offer[i] for i in range(n)]
                    pick = min(pick, offerPrice + findLowestPriceMemo(new_needs))

            dp[key] = min(notpick, pick)
            return dp[key]


        # driver code
        return findLowestPriceMemo(needs)

