/**
 * @param {number[]} price
 * @param {number[][]} special
 * @param {number[]} needs
 * @return {number}
 */
var shoppingOffers = function(price, special, needs) {
    const memo = new Map();

    function minCost(needs) {
        const key = needs.join(',');

        if (memo.has(key)) {
            return memo.get(key);
        }

        let cost = 0;

        // Calculate the cost without using any special offer
        for (let i = 0; i < price.length; i++) {
            cost += price[i] * needs[i];
        }

        // Try using each special offer
        for (const offer of special) {
            const newNeeds = [];

            for (let i = 0; i < price.length; i++) {
                if (needs[i] < offer[i]) {
                    // Cannot use this offer, break
                    newNeeds.length = 0;
                    break;
                }
                newNeeds.push(needs[i] - offer[i]);
            }

            if (newNeeds.length > 0) {
                cost = Math.min(cost, offer[offer.length - 1] + minCost(newNeeds));
            }
        }

        memo.set(key, cost);
        return cost;
    }

    return minCost(needs);
};

