class Solution {
   map<vector<int>,int> dp;
    int solve(int n,vector<int>& price, vector<vector<int>>& special, vector<int>& needs)
     {    
        if(dp.count(needs))
         return dp[needs];

       int buyDirectly = 0;
        for(int k=0; k<n; k++)
         buyDirectly += needs[k]*price[k];

        int buyWithOffer = 1e8;
         for(auto currOffer : special)
         {  
         vector<int> copy = needs;
         int isValidOffer = 1;
           for(int j=0; j<n; j++)
           {
             copy[j] -= currOffer[j];      
             if(copy[j] < 0)
              {
              isValidOffer = 0;
              break;
              }
            }
        if(isValidOffer) 
         buyWithOffer = min(buyWithOffer, currOffer[n] + solve(n,price,special,copy));
         }   
      return dp[needs] =  min({buyWithOffer,buyDirectly});
     }
public:
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        int n = price.size();
        return solve(n,price,special,needs);
    }
};
