class Solution {
public:

    map<vector<int>,int> m;

    int DFS(vector<int>& curr,vector<vector<int>>& special,vector<int>& price){
        int n = curr.size();
        if(m.find(curr)!=m.end()) return m[curr];
        int ans=0;
        for(int i=0;i<n;i++){
            ans+=(curr[i]*price[i]);
        }
        for(auto x : special){
            int canbuy=1;
            for(int i=0;i<n;i++){
                if(x[i] > curr[i]){
                    canbuy = 0;
                    break;
                }
            }
            // cout<<"canbuy is "<<canbuy<<endl;
            if(canbuy > 0){
                for(int i=0;i<n;i++){
                    curr[i]-=canbuy*x[i];
                }
                ans = min(ans,DFS(curr,special,price) + x[n]*canbuy);
                for(int i=0;i<n;i++){
                    curr[i]+=canbuy*x[i];
                }
            }
        }
        m[curr] = ans;
        return ans;
    }


    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        int n = price.size();
        vector<int> nnn(n,0);
        m[nnn]=0;
        return DFS(needs,special,price);
    }
};
