const f = (price , needs , offers , ans) => {
    let key = '' 
    for(let need of needs) key += `_${need}`;
    if(ans.has(key)) return ans.get(key);
    // console.log({key});
    let cost = 0 ; 
    for(let i=0;i<price.length;i++) cost+= (price[i]*needs[i] ); // in case of no better offers 
    // looking for better offers than original price 
    for(let offer of offers) {
        let temp = Array.from(needs) , valid = true ;// doing temp = needs create only reference to needs
        for(let i=0;i<temp.length;i++){
            temp[i]-=offer[i];
            if(temp[i]<0) valid = false ; 
        }
        if(valid) cost = Math.min(cost , offer[offer.length-1] + f(price , temp , offers , ans));
    }
    ans.set(key,cost); 
    return cost ; 
}
var shoppingOffers = function(price, offers, needs) {
    let ans = new Map() ; 
    return f(price , needs , offers , ans) ;
};


```

