# Code
```
public class Solution {
    public int ShoppingOffers(IList<int> price, IList<IList<int>> special, IList<int> needs) {
        int n = price.Count;
        Dictionary<int,int> dp = new Dictionary<int,int>();
        int getHash(int[] arr){
            int hash = 7;
            for(int i = 0;i < arr.Length;i++)
                hash = hash * 11 + arr[i];
            return hash;
        }
        int solve(int[] need){
            int hash = getHash(need);
            if(dp.ContainsKey(hash)) return dp[hash];
            int res = 0;
            for(int i = 0; i < n;i++) res+= (need[i] * price[i]);
            // try offers
            foreach(var offer in special){
                int[] newNeeds = new int[n];
                bool canUse = true;
                for(int i = 0; i < n ;i++){
                    if(need[i] >= offer[i])
                        newNeeds[i] = need[i] - offer[i];
                    else {
                        canUse = false;
                        break;
                    }
                }
                if(canUse)
                    res = Math.Min(res, offer[n] + solve(newNeeds));
            }
            dp.Add(hash,res);
            return res;
        }
        return solve(needs.ToArray());
    }
}
