```
class Solution {
public:
    int solve(int ind, vector<int>& prices, vector<vector<int>>& special, vector<int>& needs,unordered_map<string, int>& dp) {
        if (ind >= special.size()) {
            int cost = 0;
            for (int j = 0; j < needs.size(); j++) {
                cost += needs[j] * prices[j];
            }
            return cost;
        }

        string state = getKey(ind, needs);
        if (dp.count(state)) {
            return dp[state];
        }

        int minPrice = INT_MAX;

        bool validoffer = true;

        for (int j = 0; j < needs.size(); j++) {
            if (special[ind][j] > needs[j]) {
                validoffer = false;
                break;
            }
        }

        if (validoffer) {
            for(int j = 0; j < needs.size(); j++) {
                 needs[j] -= special[ind][j];
            }

            minPrice = min(minPrice, special[ind][needs.size()] + solve(ind,prices, special, needs,dp));//check if same offer can be used again

            for(int k = 0; k < needs.size(); k++) {
                needs[k] += special[ind][k];
            }
        }

        int notPickprice = solve(ind + 1, prices, special, needs,dp);//not taking current offer

        minPrice = min(minPrice, notPickprice);

        return dp[state] = minPrice;
    }

    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        unordered_map<string, int> dp;
        int ans = solve(0,price, special, needs,dp);
        return ans;
    }

    private:
    string getKey(int ind, vector<int>& needs) {
        string key = to_string(ind) + "-";
        for (int i = 0; i < needs.size(); i++) {
            key += to_string(needs[i]) + "-";
        }
        return key;
    }
};

```

