class Solution {
public:
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        vector<unordered_map<int, int>> cache(special.size());
        // vector<map<vector<int>, int>> cache(special.size());
        int res = calc(price, special, needs, cache, 0);
        return res;
    }

    int calc(const vector<int>& price, const vector<vector<int>>& special, vector<int>& needs, vector<unordered_map<int, int>>& cache/*vector<map<vector<int>, int>>& cache*/, int st) {
        int key = 0;
        // for each of the 6 'need' values, 0<=need<=10; it can be stored in 4 bits
        for (int need: needs) {
            key = (key | need) << 4;
        }
        unordered_map<int, int>::iterator it = cache[st].find(key);
        // map<vector<int>, int>::iterator it = cache[st].find(needs);
        if (it != cache[st].end()) return it -> second;

        // tot amt without taking any spl offers
        int no_offer = 0;
        for (int j = 0; j < needs.size(); j++) {
            no_offer += price[j] * needs[j];
        }

        // 'st' is for pruning/optimization; if we have reached offer #2 after processing offer #1 (multiple or one time), no need to process offer #1 again as order of processing doesn't matter, i.e. we have processed offer #1 as many times as needed before processing offer #2 so no need to process again
        int res = no_offer;
        for (int i = st; i < special.size(); i++) {
            if (no_offer < special[i][price.size()]) continue;

            vector<int> new_needs;
            bool neg = false;
            for (int j = 0; j < needs.size(); j++) {
                int rem = needs[j] - special[i][j];
                if (rem < 0) {
                    neg = true;
                    break;
                }
                new_needs.push_back(rem);
            }

            int take_offer = neg ? INT_MAX : special[i][price.size()] + calc(price, special, new_needs, cache, i);
            res = min(res, take_offer);
        }
        return cache[st][key] = res;
        // return cache[st][needs] = res;

    }
};
