    data(int i, vector<int> d)
    {   
        index = i; 
        data1 = d;

    }

    // Match both first and last names in case
    // of collisions.
    bool operator==(const struct data& d) const
    {
        return index== d.index and data1==d.data1;
    }
};

struct hash_pair{

    size_t operator()(const struct data& d) const
    {
        return d.index;
    }


};

class Solution {
public:

    unordered_map<struct data,int,hash_pair> dp;
    int solve(vector<vector<int>>& special,vector<int>& price,vector<int>  needs,int n,int x)
    {   
        vector<int> temp;
        for(int i=0;i<x;i++)
        {
            temp.push_back(needs[i]);
        }

        struct data p1(n,temp);





        if(n==0)
        {
            int cost=0;
            for(int i=0;i<x;i++)
            {
                cost+=price[i]*needs[i];
            }



            return dp[p1] = cost;
        }

        if(dp.find(p1)!=dp.end())
        {
            return dp[p1];
        }

        int flag=1;
        int cost=0;
