class Solution:
    def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:

        @functools.lru_cache(None)
        def total(need):
            t = 0
            for n, p in zip(need, price):
                t += n * p
            return t
        @functools.lru_cache(None)
        def dp(i, n):
            ans = total(n)
            for j in range(i, len(special)):
                t = list(n)
                for k in  range(len(t)):
                    if special[j][k] > t[k] :
                        break
                    else:
                        t[k] -= special[j][k]
                else:
                    ans  = min(ans, special[j][-1] + dp(j, tuple(t)))
            return ans


        return dp(0, tuple(needs))
