class Solution {
public:
    bool isValid(vector<vector<int>>& special, vector<int>& needs,int idx)
    {
        for(int i=0;i<needs.size();i++)
        {
            if(needs[i]-special[idx][i]<0)
            return false;
        }
        return true;
    }



    int solve(vector<int>& price, vector<vector<int>>& special, vector<int>& needs,int idx,int n)
    {
        if(idx==special.size())
        {
            int total=0;
            for(int i=0;i<needs.size();i++)
            {
                total=total+needs[i]*price[i];
            }
            return total;
        }

        int notpick=1e9,pick=1e9;

        notpick=solve(price,special,needs,idx+1,n);

        if(isValid(special,needs,idx))
        {
            for(int i=0;i<needs.size();i++)
        {
            needs[i]=needs[i]-special[idx][i];
        }
            pick=special[idx][n]+solve(price,special,needs,idx,n);

            for(int i=0;i<needs.size();i++)
        {
            needs[i]=needs[i]+special[idx][i];
        }
        }

        return min(pick,notpick);
    }
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
        int n=needs.size();
        return solve(price,special,needs,0,n);
    }
};
