class Solution {
    public int shoppingOffers(List<Integer> price, List<List<Integer>> special, List<Integer> needs) {
        int totalNeed=0 ;
        for (Integer need:needs) totalNeed+=need;
        return shoppingOffers(price,special,needs,totalNeed,new HashMap());
    }

    public int shoppingOffers(List<Integer> price, List<List<Integer>> special, List<Integer> needs, int totalNeed,HashMap<Integer,Integer> mamo) {
        if (totalNeed==0) return 0;
        int n=price.size() , best=Integer.MAX_VALUE , mamoKey = getMamoKey(needs);
        if (mamo.containsKey(mamoKey)) return mamo.get(mamoKey);
        for (int i=0;i<n;i++){
            if (needs.get(i)>0) {
                needs.set(i,needs.get(i)-1);
                best = Math.min(best, price.get(i)+shoppingOffers(price,special,needs,totalNeed-1,mamo));
                needs.set(i,needs.get(i)+1);
            }
        }
        for (List<Integer> s:special){
            if (offerPossible(s,needs)){
                for (int i=0;i<n;i++){
                    needs.set(i,needs.get(i)-s.get(i));
                    totalNeed-=s.get(i);
                }
                best = Math.min(best, s.get(n) + shoppingOffers(price,special,needs,totalNeed,mamo));
                for (int i=0;i<n;i++){
                    needs.set(i,needs.get(i)+s.get(i));
                    totalNeed+=s.get(i);
                }
            }
        }
        mamo.put(mamoKey,best);
        return mamo.get(mamoKey);
    }

    public int getMamoKey( List<Integer> needs){
        int key=0, radix=1;
        for (int i=0;i<needs.size();i++) {
            key+=needs.get(i)*radix;
            radix*=11;
        }
        return key;
    }
    public boolean offerPossible(List<Integer> s, List<Integer> needs){
        for (int i=0;i<needs.size();i++) if (s.get(i)>needs.get(i)) return false;
        return true;
    }
}
