class Solution {
public:
    int f(vector<int>&price,vector<vector<int> > & special,vector<int>needs,map<vector<int>, int >&dp)
    {
        if(dp.find(needs)!=dp.end())
        {
            return dp[needs];
        }
        else
        {
            //go on exploring all offers from current
            int mini=INT_MAX;
            for(auto x:special)
            {
                vector<int>newneed;
                bool can=true;
                for(int i=0;i<needs.size();i++) // calculate that the current offer can be applied or not
                {
                    if(needs[i]>=x[i])
                    {
                       newneed.push_back(needs[i]-x[i]);
                    }
                    else
                    {
                       can=false;
                       break;
                    }
                }

            if(can) // if this offers could be applied....
            {
               int current=x[needs.size()]+f(price,special,newneed,dp);
               mini=min(mini,current);
            }
            else // if this offer cant be applied.....
            {
                int current=0; // find the raw cost of remaining items
               for(int i=0;i<needs.size();i++) 
               {
                  current+=needs[i]*price[i];
               }
               mini=min(mini,current);
            }

            }
            // for the case when no offers are taken by choice....
            int current=0;
            for(int i=0;i<needs.size();i++)
            {
               current+=price[i]*needs[i];
            }
            mini=min(current,mini);
            return dp[needs]=mini;
        }
    }
    int shoppingOffers(vector<int>& price, vector<vector<int>>& special, vector<int>& needs) {
    map<vector<int>, int > dp;
        int ans=f(price,special,needs,dp);
        return ans;
    }
};
