class Solution:
    def shoppingOffers(self, price: List[int], special: List[List[int]], needs: List[int]) -> int:

        n = len(price)

        for i,p in enumerate(price):
            temp = [0]*(n+1)
            temp[i] = 1
            temp[-1] = p
            special.append(temp)


        @cache
        def backtrack(needs):
            if not any(needs):
                return 0

            ans = inf
            for offer in special:
                if not any(item > need for item,need in zip(offer, needs)):
                    ans = min(ans, backtrack(tuple(need-item for item,need in zip(offer, needs))) + offer[-1])
            return ans

        return backtrack(tuple(needs))
