class Solution {
public:
    int memo[15][15][15][15][15][15] = {0};

    int shoppingOffers(vector<int> &price, vector<vector<int>> &special, vector<int> &needs) {
        int n = price.size();
        vector<vector<int>> specials;

        for (int i = 0; i < special.size(); i++) {
            int sum = 0;
            for (int j = 0; j < n; j++) sum += special[i][j] * price[j];
            if (sum > special[i].back()) specials.push_back(special[i]);
        }

        vector<int> needs2(6, 0);
        for (int i = 0; i < n; i++) needs2[i] = needs[i];

        // do dfs + memoization
        return dfs(price, specials, needs2);
    }

    int dfs(vector<int> &price, vector<vector<int>> &specials, vector<int> &needs) {
        int n = price.size();

        if (memo[needs[0]][needs[1]][needs[2]][needs[3]][needs[4]][needs[5]] != 0) return memo[needs[0]][needs[1]][needs[2]][needs[3]][needs[4]][needs[5]];

        int &x = memo[needs[0]][needs[1]][needs[2]][needs[3]][needs[4]][needs[5]];
        int res = 0;

        for (int z = 0; z < n; z++) res += price[z] * needs[z];

        for (const auto combo: specials) {
            int choose = 1;
            for (int k = 0; k < n; k++) {
                if (combo[k] > needs[k]) {
                    choose = 0;
                    break;
                }
            }

            if (choose) {
                for (int p = 0; p < n; p++) {
                    needs[p] -= combo[p];
                }

                res = min(res, dfs(price, specials, needs) + combo.back());
                for (int q = 0; q < n; q++) {
                    needs[q] += combo[q];
                }
            }
        }

        return x = res;
    }
};
