        int r=grid.length, c=grid[0].length;
        if(i==r||j==c) return Integer.MAX_VALUE;
        if(i==r-1&&j==c-1) return grid[i][j];
        int down=dfs(i+1,j,grid), right=dfs(i,j+1,grid);
        return Math.min(down,right)+grid[i][j];
    }
