```
class Solution {
public:
    int minPathSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        vector<int> prevRow(m+1,0);

        for(int i=n-1;i>=0;i--){
            vector<int> currRow(m+1,0);
            for(int j=m-1;j>=0;j--){
                if(i==n-1 and j==m-1){
                    currRow[j]=grid[i][j];
                }else{
                    int down=INT_MAX,right=INT_MAX;
                    if(i+1<n)
                        down=prevRow[j];
                    if(j+1<m)
                        right=currRow[j+1];
                    currRow[j]=grid[i][j]+min(down,right);
                }
            }
            prevRow=currRow;
        }

        return prevRow[0];
    }
};
```

