class Solution {
public:
    int minPathSum(vector<vector<int>>& grid) {
        //bottom up approach for the problem
        //space complexity will be O(n*m)
        int row = grid.size();
        if(row == 0)    return 0;
        int col = grid[0].size();

        vector<vector<int>> dp(row,vector<int> (col,0));


        for(int i=0;i<row;i++){
            for(int j=0;j<col;j++){

                if(i == 0 && j == 0){
                    dp[i][j] = grid[i][j];
                }else{
                    if(i == 0){
                        dp[i][j] = grid[i][j] + dp[i][j-1];
                    }else{
                        if(j == 0){
