class Solution {
public:

    int solve(vector<vector<int>>& grid, int m, int n, vector<vector<int>>& dp)
    {
        if(m==0 && n==0)
            return dp[m][n];
        if(dp[m][n]!=-1)
            return dp[m][n];
        if(m==0 && n!=0)
            return dp[m][n] = grid[m][n]+solve(grid, m, n-1, dp);

        if(n==0 && m!=0)
            return dp[m][n] =grid[m][n]+solve(grid, m-1, n, dp);

        else
            return dp[m][n] =grid[m][n] + min(solve(grid, m-1, n, dp), solve(grid, m, n-1, dp));
    }
    int minPathSum(vector<vector<int>>& grid) {

        int m=grid.size();
        int n=grid[0].size();
        for(int i=0;i<m;i++)
