class Solution {
private:
    int topDown(int i, int j, int n, int m, vector<vector<int>> &grid, vector<vector<int>> &dp){
        //checking wheter we are inside the matrix or not. if not, we return INT_MAX as we are checking for minimum.
        if(i >= n || j >= m){
            return INT_MAX;
        }
        //cost to reach last cell from last cell is the value present in the last cell
        if(i == n-1 && j == m-1){
            return grid[n-1][m-1];
        }

        if(dp[i][j] != -1) return dp[i][j]; // if not -1 then the value is already computed no need to calculate again

        int down = topDown(i+1, j, n, m, grid, dp); // value which function will return when we go down
        int right = topDown(i, j+1, n, m, grid, dp);// value which function will get when return we go right

        return dp[i][j] = grid[i][j] + min(down, right); // since we want minimum we will choose minimum from down and right + value at the present cell and store it in dp matrix
    }
public:
