    public int MinPathSum(int[][] grid) {
        for(int i = 0; i < grid.Length; i++) {
            for (int j = 0; j < grid[i].Length; j++) {
                if (i == 0 && j == 0)
                    continue;

                if (i == 0) {
                    grid[i][j] += grid[i][j - 1];
