class Solution {
public:
    int minPathSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        vector<int> dp(m, INT_MAX);
        dp[0] = 0;

        for(int i=0; i<n; i++) {
            vector<int> temp(m);
            for(int j=0; j<m; j++) {
                temp[j] = grid[i][j] + min(dp[j], (j > 0 ? temp[j - 1] : INT_MAX));
            }
            dp = temp;
        }
        return dp[m - 1];
    }
};
