func canJump(nums []int) bool {
    if len(nums) == 0 {
        return false
    }

    if len(nums) == 1 {
        return true
    }

    lastIdx := len(nums) - 1

    record := make(map[int]bool)

    for i := lastIdx - 1; i > -1; i-- {
        if nums[i] >= lastIdx - i {
            record[i] = true
        } else {
            for key, _ := range record {
                if nums[i] >= key - i {
                    record[i] = true
                    break
                }
            }
        }
    }

    return record[0]
}
