class Solution {

    public boolean canJump(int[] nums) {
        int n = nums.length;
        int maxReach = -1;
        int globalMaxReach = -1;
        for(int i=0;i<n-1;i++) {
            maxReach = i + nums[i];
            globalMaxReach = Math.max(globalMaxReach,maxReach);
            if (i == globalMaxReach) return false;
        }
        return true;
    }
}
