class MinStack {
public:

    #define ll long long

    stack<ll int> st;
    ll int minval;

    MinStack() {
        minval=10000000000;
    }

    void push(int val) {
        if(st.size()==0){
            st.push(val);
            minval=val;
        }else if(val>=minval){
            st.push(val);
        }else{
            ll int dummy=val+(val-minval); // this value will be smaller than current min, detection point that min val changes
            st.push(dummy);
            minval=val;
        }
    }

    void pop() {
        if(st.top()>=minval){
            st.pop();
        }else{
            minval=minval + (minval-st.top()); // retrieving previous min value (for remaining elements)
            st.pop();
        }
    }

    int top() {
        if(st.top()>=minval){
            return st.top();
        }
        return (int)minval;
    }

    int getMin() {
        return (int)minval;
    }
};
