class MinStack {
    private int size = 0;
    private Node head = null;

    class Node {
        int val;
        // every node contains min value at the moment it was pushed
        int min;
        Node next;
        Node(int v, int m) {val = v; min = m;}
    }

    public void push(int x) {
        int curMin = getMin();
        int newMin = x < curMin ? x : curMin;

        Node n = new Node(x, newMin);
        n.next = head;

        head = n;
        size++;
    }

    public void pop() {
        if (size <= 0)
            return;

        head = head.next;
        size--;
    }

    public int top() {
        if (size <= 0)
            return Integer.MAX_VALUE;

        return head.val;
    }

    public int getMin() {
        if (size <= 0)
            return Integer.MAX_VALUE;

        return head.min;
    }
}

