import java.util.Stack;

class MinStack {
    Stack<Integer> s; // Stack to store the elements
    Stack<Integer> min; // Stack to store the minimum elements

    /**
     * Initialize the MinStack.
     */
    public MinStack() {
        s = new Stack<>();
        min = new Stack<>();
    }

    /**
     * Pushes an element onto the stack.
     * @param val The value to be pushed.
     */
    public void push(int val) {
        s.push(val); // Push the value onto the main stack

        // If the minimum stack is empty or the new value is less than or equal to the current minimum,
        // push the new value onto the minimum stack
        if (min.isEmpty() || min.peek() >= s.peek()) {
            min.push(val);
        }
    }

    /**
     * Removes the top element of the stack.
     */
    public void pop() {
        int val = s.pop(); // Pop the top value from the main stack

        // If the popped value is the current minimum, remove it from the minimum stack
        if (min.peek() == val) {
            min.pop();
        }
    }

    /**
     * Returns the top element of the stack.
     * @return The top element of the stack, or -1 if the stack is empty.
     */
    public int top() {
        if (s.isEmpty()) {
            return -1;
        }
        return s.peek(); // Return the top value from the main stack
    }

    /**
     * Retrieves the minimum element in the stack.
     * @return The minimum element in the stack, or -1 if the stack is empty.
     */
    public int getMin() {
        if (min.isEmpty()) {
            return -1;
        }
        return min.peek(); // Return the top value from the minimum stack
    }
}

