class MinStack {
    private List<Integer> stack = new ArrayList();    //for pushing into stack
    private List<Integer> stackWithMins = new ArrayList(); //to reduce the time complexity of finding min element

    public MinStack() {}

    public void push(int val) {        
        if (stackWithMins.isEmpty()) {
            stackWithMins.add(val); //pushing if the min-stack is empty
        } else if (val <= stackWithMins.get(stackWithMins.size()-1)) { //if the min-stack is empty we compare it with the top element in the min-stack because the top element is less than all the previous elements due to the same condition
            stackWithMins.add(val); 
        }

        stack.add(val); // normal pushing into stack
    }

    public void pop() {
        int poppedValue = stack.remove(stack.size()-1); // removing the top element from stack
        int minValue = stackWithMins.get(stackWithMins.size()-1); // getting the top element from min-stack

        if (poppedValue == minValue) {
            stackWithMins.remove(stackWithMins.size()-1); //if the top element from min-stack is equal to the min element, then we need to pop it
        }
    }

    public int top() {
        return stack.get(stack.size()-1); //getting the top element
    }

    public int getMin() {
        return stackWithMins.get(stackWithMins.size()-1); // getting the top element from min-stack
    }
}
