class Twitter {

    HashMap<Integer, User> users;
    int timestamp;

    /** Initialize your data structure here. */
    public Twitter() {
        users = new HashMap();
        timestamp = 0;
    }

    /** Compose a new tweet. */
    public void postTweet(int userId, int tweetId) {
        //add user, if not added
        users.putIfAbsent(userId, new User(userId));

        //add new tweet in this user account
        users.get(userId).addTweet(tweetId, timestamp++);
    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. Each item in the news feed must be posted by users who the user followed or by the user herself. Tweets must be ordered from most recent to least recent. */
    public List<Integer> getNewsFeed(int userId) {
        //add user, if not added
        users.putIfAbsent(userId, new User(userId));

        //minheap, heapify on timestamp 
        PriorityQueue<Tweet> minHeap = new PriorityQueue<Tweet>((a, b) -> (a.timestamp - b.timestamp));

        //add all 10 recent tweet from its account
        addTweetsInMinHeap(minHeap, users.get(userId).tweets);

        //add recent tweet from all user, whom this user follow
        for(int followee : users.get(userId).followees){
            addTweetsInMinHeap(minHeap, users.get(followee).tweets);
        }

        List<Integer> tweetIdList = new ArrayList();
        //make the tweet id list, from minheap
        while(!minHeap.isEmpty()){
            tweetIdList.add(minHeap.remove().tweetId);
        }

        //as minheap, tweetIdList is in increasing timestamp
        //reverse for decreasing timestamp order
        Collections.reverse(tweetIdList);

        return tweetIdList;
    }

    private void addTweetsInMinHeap(PriorityQueue<Tweet> minHeap, LinkedList<Tweet> tweets){
        for(Tweet tweet : tweets){
            //add tweet
            minHeap.add(tweet);

            //except 10 recent tweet, remove older one tweet
            if(minHeap.size() > 10){
                minHeap.remove();
            }
        }
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    public void follow(int followerId, int followeeId) {
        //add follwer, if not added
        users.putIfAbsent(followerId, new User(followerId));
        //add followee, if not added
        users.putIfAbsent(followeeId, new User(followeeId));

        //follow
        users.get(followerId).follow(followeeId);
    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    public void unfollow(int followerId, int followeeId) {
        //check follower exist or not
        if(!users.containsKey(followerId)) return;

        //unfollow
        users.get(followerId).unfollow(followeeId);
    }
}

class User {
    //set of userId, this user follow 
    HashSet<Integer> followees;
    //list of 10 recent tweet 
    LinkedList<Tweet> tweets;
    int userId;

    public User(int userId){
        this.userId = userId;
