import java.util.*;

class Twitter {

    private class Tweet {
        int id;
        int time;

        Tweet(int id, int time) {
            this.id = id;
            this.time = time;
        }
    }

    private class Iter {
        int i;
        List<Tweet> list;

        Iter(List<Tweet> list) {
            i = 0;
            this.list = list;
        }

        Tweet peek() {
            return list.get(i);
        }

        boolean hasNext() {
            return i < list.size();
        }

        Tweet next() {
            return list.get(i++);
        }
    }

    private Map<Integer, Set<Integer>> follows;
    private Map<Integer, List<Tweet>> tweets;
    private int time;

    public Twitter() {
        follows = new HashMap<>();
        tweets = new HashMap<>();
        time = 0;
    }

    public void postTweet(int userId, int tweetId) {
        validateUser(userId);
        Tweet tweet = new Tweet(tweetId, time++);
        tweets.get(userId).add(0, tweet);
    }

    public List<Integer> getNewsFeed(int userId) {
        validateUser(userId);

        PriorityQueue<Iter> queue = new PriorityQueue<>((x, y) -> {
            return -(x.peek().time - y.peek().time);
        });

        if (tweets.get(userId).size() > 0) {
            queue.add(new Iter(tweets.get(userId)));
        }

        for (int followee : follows.get(userId)) {
            if (tweets.get(followee).size() > 0) {
                queue.add(new Iter(tweets.get(followee)));
            }
        }

        List<Integer> ans = new ArrayList<>();
        while (queue.size() > 0 && ans.size() < 10) {
            Iter item = queue.remove();
            ans.add(item.next().id);
            if (item.hasNext()) queue.add(item);
        }

        return ans;
    }

    public void follow(int followerId, int followeeId) {
        validateUser(followerId);
        validateUser(followeeId);
        if (followerId == followeeId) return;
        follows.get(followerId).add(followeeId);
    }

    public void unfollow(int followerId, int followeeId) {
        validateUser(followerId);
        validateUser(followeeId);
        follows.get(followerId).remove(followeeId);
    }

    private void validateUser(int userID) {
        tweets.putIfAbsent(userID, new LinkedList<>());
        follows.putIfAbsent(userID, new HashSet<>());
    }
}
