class Twitter {
public:
    Twitter() {

    }
    // mapping follower with followee
    map<int,set<int>>follower_followee;

    // used list of pair<userId,tweetId> just because of push_front. For getting most recent tweets;
    list<pair<int,int>>tweets;
    void postTweet(int userId, int tweetId) {
        tweets.push_front({userId,tweetId});
    }

    vector<int> getNewsFeed(int userId) {
        auto itr = tweets.begin();
        int count = 0;
        vector<int>ans;
        while(count<10 and itr!=tweets.end())
        {
            pair<int,int>temp = *itr;
            int user = temp.first;
            int tweet = temp.second;
            // cout<<user<<" "<<tweet<<"\n";
            if(user == userId)
            {
                // adding own feed
                ans.push_back(tweet);
                count++;
            }
            else if(follower_followee[userId].find(user) != follower_followee[userId].end())
            {
                // adding followee's feed
                ans.push_back(tweet);
                count++;
            }
            itr++;
        }
        // cout<<"\n";
        return ans;
    }

    void follow(int followerId, int followeeId) {
