class Twitter {
    class Tweet{
        int  id;
        int createdAt;
        public Tweet(int id, int createdAt){
            this.id = id;
            this.createdAt = createdAt;
        }
    }

    HashMap<Integer, Set<Integer>> followed;// <key,Value< : <UserId, List of followee id (means person user is following)>
    HashMap<Integer, List<Tweet>> tweets; //<Key,Value> : <UserId, List of TweetObject tweeted by that person>
    int timeStamp = 0;
    int feedSize = 10;
    /** Initialize your data structure here. */
    public Twitter() {
        followed = new HashMap<>();
        tweets = new HashMap<>();
    }

    /** Compose a new tweet. */
    public void postTweet(int userId, int tweetId) {

        //Adding it to UserTweet Map 
        if(!tweets.containsKey(userId))
            tweets.put(userId, new ArrayList<>());
        tweets.get(userId).add(new Tweet(tweetId, timeStamp++));

        //Need put that person to follow himself so as to get feeds
        if(!followed.containsKey(userId))
            followed.put(userId, new HashSet<>());
        followed.get(userId).add(userId);

    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. Each item in the news feed must be posted by users who the user followed or by the user herself. Tweets must be ordered from most recent to least recent. */
    public List<Integer> getNewsFeed(int userId) {
        //MinHeap will give us 10 latest tweets from the tweets he follows
        PriorityQueue<Tweet> minHeap = new PriorityQueue<>((t1, t2)-> t1.createdAt-t2.createdAt);

        // We need to get all  people that userId follows.
        Set<Integer> followedByCurrentUserId = followed.get(userId);

        List<Integer> result = new ArrayList<>();
        if(followedByCurrentUserId !=null && followedByCurrentUserId.size()>0){

            //iterate these users tweets and send to minheap to get the latest 10 tweets overall
            for(int f : followedByCurrentUserId ){ 
                List<Tweet> fTweets = tweets.get(f);
                if(fTweets == null)  // go to next user
                    continue;
                //else iterate over that person's tweets
                for(Tweet t : fTweets){
                    if(minHeap.size()< feedSize)
                        minHeap.add(t);
                    else {
                       if(t.createdAt > minHeap.peek().createdAt){
                            minHeap.poll();
                            minHeap.add(t);
                        } 
                    }
                }

            }

            while(!minHeap.isEmpty()) 
                result.add(0, minHeap.poll().id);
            return result;
        }
        return result;
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    public void follow(int followerId, int followeeId) {
        if(!followed.containsKey(followerId)) 
            followed.put(followerId, new HashSet<>());
      followed.get(followerId).add(followeeId);
    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    public void unfollow(int followerId, int followeeId) {
        if(followed.containsKey(followerId) && followerId != followeeId)
            followed.get(followerId).remove(followeeId);
    }
}

