class Twitter
{
public:
    map<int, priority_queue<pair<int, int>, vector<pair<int, int>>, greater<pair<int, int>>>> user_tweets; // userId -> min heap of (timestamp, tweetId) pairs
    map<int, set<int>> followers;                                                                          // followerId -> set of followeeIds
    int timestamp = 0;

    Twitter() {}

    void postTweet(int userId, int tweetId)
    {
        user_tweets[userId].push({timestamp, tweetId}); // stores the tweet with (timestamp, tweetId) pair in min heap
        timestamp++;
        if (user_tweets[userId].size() > 10) // keeping a maximum of 10 recent posts of a user
            user_tweets[userId].pop();
    }

    void copyToMaxHeap(priority_queue<pair<int, int>> &maxHeap, int userId)
    {
        vector<pair<int, int>> temp;
        while (!user_tweets[userId].empty())
        {
            int time = user_tweets[userId].top().first;
            int tweetId = user_tweets[userId].top().second;
            user_tweets[userId].pop();
            maxHeap.push({time, tweetId});
            temp.push_back({time, tweetId});
        }
        for (auto entry : temp)
        {
            user_tweets[userId].push({entry.first, entry.second});
        }
    }

    vector<int> getNewsFeed(int userId)
    {
        vector<int> result;
        priority_queue<pair<int, int>> maxHeap;

        // Add tweets from the user itself
        copyToMaxHeap(maxHeap, userId);

        // Add posts by followers
        for (auto followeeId : followers[userId])
        {
            copyToMaxHeap(maxHeap, followeeId);
        }

        // Retrieve the 10 most recent tweets
        int count = 0;
        while (!maxHeap.empty() && count < 10)
        {
            result.push_back(maxHeap.top().second);
            maxHeap.pop();
            count++;
        }

        return result;
    }

    void follow(int followerId, int followeeId)
    {
        followers[followerId].insert(followeeId);
    }

    void unfollow(int followerId, int followeeId)
    {
        followers[followerId].erase(followeeId);
    }
};

```

