class Twitter {
private: 
    int counter=1; /*tweet number*/
    unordered_map<int,unordered_set<int>>fl; /*follow List*/
    unordered_map<int,vector<vector<int>>>tw; /*{tweet, counter}*/

    struct comp{
      bool operator()(vector<int>& a, vector<int>& b) {
          return a[1]<b[1];
      }
    };

public:
    Twitter() {
    }

    void postTweet(int userId, int tweetId) {
        tw[userId].push_back({tweetId,counter});
        counter++;
    }

    vector<int> getNewsFeed(int userId) {
        priority_queue<vector<int>,vector<vector<int>>,comp> pq;
        for(auto& a : tw[userId]) 
            pq.push({a[0],a[1]});
        for(auto& a : fl[userId]) {
            for(auto& b : tw[a]) 
                pq.push({b[0],b[1]});
        }
        vector<int>list;
        int size=(pq.size()<10)?pq.size():10;
        while(size-->0) {
            int tweet=pq.top()[0];
            pq.pop();
            list.push_back(tweet);
        }
        return list;
    }

    void follow(int followerId, int followeeId) {
        if(followerId!=followeeId)
            fl[followerId].insert(followeeId);
    }

    void unfollow(int followerId, int followeeId) {
        fl[followerId].erase(followeeId);
    }
};

