	// A DP that signify the relation between two user
    bool dp[501][501] = {false};

	// Linked-List of Tweets
    ListNode *head;
public:
    Twitter() {}

    void postTweet(int userId, int tweetId) {

		// First Tweet ever
		if(!head) head = new ListNode(userId, tweetId);

		// Add new node ahead of curretn head
        else{
            ListNode *tp = new ListNode(userId, tweetId);
            tp->next = head;
            head = tp;    
        }
    }

    vector<int> getNewsFeed(int userId) {
        ListNode *cur = head;

        vector<int> ans;

		// Iterate through the linked-list till either the ans size become 10 or it reaches the end
        while((cur != NULL) && (ans.size() < 10)){

			// The feed posted by ther user himself and the peopele he follow will only be visible
            if((userId == cur->uid) || (dp[userId][cur->uid])) ans.push_back(cur->tid);

            cur = cur->next;
