// HashMap + PriorityQueue + Set + Queue
class Twitter {
    private long globalTimestamp;
    private Map<Integer, Queue<Tweet>> tweetDB;
    private Map<Integer, Set<Integer>> followingDB;

    /** Initialize your data structure here. */
    public Twitter() {
        globalTimestamp = Integer.MIN_VALUE;
        tweetDB = new HashMap<>();
        followingDB = new HashMap<>();
    }

    /** Compose a new tweet. */
    public void postTweet(int userId, int tweetId) {
        Queue<Tweet> tweets = tweetDB.getOrDefault(userId, new ArrayDeque<>());
        if (tweets.isEmpty()) {
            tweetDB.put(userId, tweets);
        }
        if (tweets.size() >= 10) {
            tweets.poll();  // remove old tweet
        }
        tweets.add(new Tweet(tweetId));
    }

    /** Retrieve the 10 most recent tweet ids in the user's news feed. Each item in the news feed must be posted by users who the user followed or by the user herself. Tweets must be ordered from most recent to least recent. */
    public List<Integer> getNewsFeed(int userId) {
        PriorityQueue<Tweet> pq = new PriorityQueue<>(11, (t1, t2) -> Long.compare(t1.timestamp, t2.timestamp));
        Set<Integer> followees = followingDB.getOrDefault(userId, new HashSet<>());
        if (followees.isEmpty()) {
            followees.add(userId);
        }
        for (Integer followee : followees) {
            Queue<Tweet> tweets = tweetDB.get(followee);
            if (tweets == null) continue;
            for (Tweet tweet : tweets) {
                pq.add(tweet);
                if (pq.size() > 10) {
                    pq.poll();
                }
            }
        }
        List<Integer> feed = new ArrayList<>(pq.size());
        while (!pq.isEmpty()) {
            feed.add(pq.poll().tweetId);
        }
        Collections.reverse(feed);
        return feed;
    }

    /** Follower follows a followee. If the operation is invalid, it should be a no-op. */
    public void follow(int followerId, int followeeId) {
        Set<Integer> followees = followingDB.getOrDefault(followerId, new HashSet<>());
        if (followees.isEmpty()) {
            followingDB.put(followerId, followees);
            followees.add(followerId);
        }
        followees.add(followeeId);
    }

    /** Follower unfollows a followee. If the operation is invalid, it should be a no-op. */
    public void unfollow(int followerId, int followeeId) {
        if (followerId == followeeId) return;
        Set<Integer> followees = followingDB.get(followerId);
        if (followees == null) return;
        followees.remove(followeeId);
    }

    class Tweet {
        int tweetId;
        long timestamp;
        Tweet(int tweetId) {
            this.tweetId = tweetId;
            this.timestamp = globalTimestamp++;
        }
    }
}

